<?php $conn = new mysqli("localhost", "root", "", "my_database"); if ($conn->connect_error) { die("Connection failed: " . $conn->connect_error); } $conn->query("DROP TABLE IF EXISTS pc_status"); $conn->query("CREATE TABLE pc_status (id INT(11) AUTO_INCREMENT PRIMARY KEY, lab_id INT(11) NOT NULL, pc_number INT(11) NOT NULL, status VARCHAR(50) NOT NULL, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, UNIQUE KEY lab_pc (lab_id, pc_number))"); echo "<h1>Database table fixed!</h1><p>Now go to <a href=\"computer_control.php\">Computer Control</a> and mark PCs as maintenance.</p>"; $conn->close(); ?>
